import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Interface pour une question
interface Question {
  id: number;
  text: string;
}

// Interface pour le composant
const QuestionPoll: React.FC = () => {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [message, setMessage] = useState<string>('');

  // Charger les questions au montage du composant
  useEffect(() => {
    axios.get('http://localhost:3000/questions')
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des questions !', error);
        setMessage('Impossible de charger les questions.');
      });
  }, []);

  // Gérer le vote
  const handleVote = (questionId: number, answer: 'Oui' | 'Non') => {
    axios.post('http://localhost:3000/votes', { questionId, answer })
      .then(response => {
        setMessage(`Votre vote "${answer}" pour la question ${questionId} a été enregistré.`);
        // Optionnel : désactiver les boutons pour cette question après le vote
      })
      .catch(error => {
        console.error('Erreur lors de l\'enregistrement du vote !', error);
        setMessage('Une erreur est survenue lors du vote.');
      });
  };

  return (
    <div>
      <h1>Sondage</h1>
      {message && <p>{message}</p>}
      {questions.length > 0 ? (
        <ul>
          {questions.map((question) => (
            <li key={question.id} style={{ marginBottom: '10px' }}>
              <span>{question.text}</span>
              <button onClick={() => handleVote(question.id, 'Oui')} style={{ marginLeft: '10px' }}>Oui</button>
              <button onClick={() => handleVote(question.id, 'Non')} style={{ marginLeft: '5px' }}>Non</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucune question à afficher.</p>
      )}
    </div>
  );
};

export default QuestionPoll;
